        public static bool CheckNumberPassword(string password)
        {
            if (password == "")
                return false;
            foreach (char c in password)
                if (!((byte)c >= 48 && (byte)c <= 57))
                    return false;
            return true;
        }
        public static bool InvalidCharacters(string Name)
        {


            if (Name.Length > 16)
                Name = Name.Substring(0, 16);
            if (Name == "")
                return false;
            foreach (char c in Name)
                if (Conquer_Online_Server.ServerBase.Kernel.InvalidCharacters.Contains(c) || (byte)c < 48)
                    return true;
            return true;


        }

        public static bool InvalidCharacters2(string Name)
        {
            var rdr = new MySqlReader(new MySqlCommand(MySqlCommandType.SELECT).Select("entities").Where("name", Name));
            if (rdr.Read())
            {
                rdr.Close();
                // Conquer_Online_Server.ServerBase.Kernel.InvalidCharacters.Contains(Name);
                return false;

            }
            rdr.Close();
            return true;


        }
        public static bool CheckNumberPassword2(string password)
        {
            if (password == "")
                return false;
            foreach (char c in password)
                if (!((byte)c >= 90 && (byte)c <= 150))
